INSERT INTO `component` VALUES ('7195', '冰川水', 'Glacier water', '', 'skin:component:usage:57203688cb465', '冰川水是地表上长期存在并能自行运动的天然冰体。由大气固体降水经多年积累而成，是地表重要的淡水资源。冰川水的珍贵，在于其含氘量极低，被中外科学界称为“生命之水”。', '0', '0', '0', '0', '1461740292');
