INSERT INTO `component` VALUES ('7158', '甲酸', 'FORMIC ACID', '', 'skin:component:usage:572036b48a5b3', '《化妆品安全技术规范2015版》中作为防腐剂使用，化妆品使用时的最大允许浓度：0.5%（以酸计）。', '0', '0', '0', '0', '1461740292');
INSERT INTO `component` VALUES ('7157', '尿酸', 'URIC ACID', '', 'skin:component:usage:5720363bebb5a,skin:component:usage:57203683b3e52,skin:component:usage:572039485745f', '尿酸为嘌呤最简单的衍生物之一，有抗氧性，可用作食品和化妆品中的抗氧剂，是化妆品中常用的调理型生化助剂，对人体无副作用，能加速皮肤角质层的新陈代谢速度，对皮肤功能性失调，如皮脂分泌过多、皮屑增多、瘙痒有疗效，通过改善角质层状态来保湿，对干性和粗糙皮肤者效果更明显。', '0', '0', '0', '0', '1461740292');
