INSERT INTO `component` VALUES ('7056', '蜂毒', 'BEE VENOM,', '', 'skin:component:usage:5720363ea46a9,skin:component:usage:5720369077320', '蜂毒不是毒素，而是工蜂毒腺和副腺分泌出的具有芳香气味的一种透明液体，这种液体贮存在毒囊中的垫刺里，由蛰针排出。多次实验表明，当这些毒液接触并轻蜇皮肤后，会刺激皮肤产生天然的胶原蛋白和弹性，从而使肌肤达到紧缩和光滑的效果。这种效果是累积性的，坚持使用一段时间后，皮肤会在不借助外力的情况下，自然变得紧致、光滑且充满弹性，因此在美容界里，称其为肉毒杆菌的天然替代品。', '0', '0', '0', '0', '1461740292');
