INSERT INTO `component` VALUES ('4858', '聚乙二醇-33', 'PEG-33', '', 'skin:component:usage:572039af1147f,skin:component:usage:572039dbb9286', '聚乙二醇类（PEG）主要用作保湿剂，通常以数字作为不同类型区分，如：PEG-4、PEG-100等，数字愈小，代表化学成分愈容易被皮肤吸收。聚乙二醇类具有从大气中吸收并保存水分的能力，可用作湿润剂，能够使皮肤润湿、柔软。PEG结构容易带入致癌物二噁烷，在化妆品中应用时原料纯度十分重要。', '0', '0', '0', '0', '1461740276');
