INSERT INTO `component` VALUES ('4849', '4-甲基苄亚基樟脑', '4-METHYLBENZYLIDENE CAMPHOR', '4-甲基苄亚基樟脑，3-(4-Methylbenzylidene)bornan-', 'skin:component:usage:57203a0b512d4', '针对 UVB的防晒剂，欧盟委员会的报告中指出，此成分有可能干扰内分泌，油溶性UVB吸收剂，最高吸收峰299nm。用量：≤4%，提高SPF指数；增加其它防晒剂的光稳定性，只需少量加入即可提高SPF值。可于高温时添加，一般添加量在1%即可。一般用于较高SPF防晒产品。', '0', '0', '0', '0', '1461740275');
