INSERT INTO `component` VALUES ('4369', '表皮细胞生长因子', 'Epidermal Growth Factor', 'EGF', 'skin:component:usage:57203688cb465', '表皮细胞生长因子是人体内的一种活性物质，由53个氨基组成的活性多肽，藉由刺激表皮细胞生长因子受体之酪氨酸磷酸化，达到修补增生肌肤表层细胞，据说对受伤、受损之表皮肌肤拥有绝佳之疗效。其最大特点是能够促进细胞的增殖分化，从而以新生的细胞代替衰老和死亡的细胞。EGF还能止血，并具有加速皮肤和粘膜创伤愈合，消炎镇痛，防止溃疡的功效。', '0', '0', '0', '0', '1461740273');
