INSERT INTO `component` VALUES ('2744', '高山火绒草（LEONTOPODIUM ALPINUM）提取物', 'LEONTOPODIUM ALPINUM EXTRACT', 'Edelweiss，Leontopodium alpinum，火绒草, 雪绒花,', 'skin:component:usage:57203688cb465', '能保护皮肤因长期受到烈日影响而受到的损害和提前老化的现象。雪绒花能够过滤由阳光所产生的对皮肤有害的微粒子，降低和减轻皮肤因日晒后的过敏现象，温和的安抚和镇静晒后的皮肤。', '0', '0', '0', '0', '1461740262');
