INSERT INTO `component` VALUES ('2737', '苯基苯并咪唑磺酸', 'PHENYLBENZIMIDAZOLE SULFONIC ACID', 'ENSULIZOLE', 'skin:component:usage:57203a0b512d4', '水溶性防晒剂，主要防止 UVB 之紫外线，及少数防护 UVA 之能力。阳光下会产生自由基，从而损伤皮肤DNA，严重时有致癌的可能。《化妆品安全技术规范2015版》中作为防晒剂使用，化妆品使用时的最大允许浓度：8%（以酸计）。', '0', '0', '0', '1', '1461740262');
INSERT INTO `component` VALUES ('2736', '奥克立林', 'OCTOCRYLENE', '欧托奎雷，2-ethylhexyl-2-cyano-3，3-diphenyl a', 'skin:component:usage:57203a0b512d4', '油溶性化学防晒剂，可吸收紫外线中波段在250～360的UVA和UVB，在防晒霜中经常搭配其他防晒剂一起使用，能达到较高的SPF防晒指数，不过Octocrylene暴露在阳光下会释放出氧自由基。《化妆品安全技术规范2015版》中作为防晒剂使用，化妆品使用时的最大允许浓度：10%（以酸计）。', '0', '0', '0', '0', '1461740262');
