INSERT INTO `component` VALUES ('1985', '石榴（PUNICA GRANATUM）果提取物', 'PUNICA GRANATUM FRUIT EXTRACT', '石榴提取物，红石榴提取液，红石榴多酚，石榴萃取，红石榴提取物', 'skin:component:usage:5720363ea46a9,skin:component:usage:57203683b3e52,skin:component:usage:5720369077320,skin:component:usage:57203a012bbda', '石榴皮的成分主要是鞣酸、鞣花酸、没食子酸、多种氨基酸，石榴汁维生素C含量非常高、还含有维生素B族、有机酸、糖类和微量元素。石榴籽含有石榴籽多酚。三部分共同作用，具有抗氧化、收敛、抑菌作用，还可以抑制酪氨酸酶活性，具有美白作用。', '0', '0', '0', '0', '1461740258');
