INSERT INTO `component` VALUES ('1785', '变性乙醇', 'ALCOHOL DENAT', 'ALCOHOL DENATURED，变性酒精', 'skin:component:usage:572039dbb9286', '为确保用于化妆品中之酒精无法转移为其他用途，因此经过特殊程序处理(加入不可食用成份)后之酒精，又称为 SD Alcohol (Specially Denatured alcohol)，主要为宣告其不可用以饮用，而在欧洲地区为了适用于国际名称惯例将其命名为 Alcohol Denat。SD Alcohol 仍然为酒精成份，只是据说因为经过处理，其蒸发速度较快，也因此能将化妆品的香味更快散逸而出，且停', '0', '0', '1', '0', '1461740256');
