INSERT INTO `component` VALUES ('1680', '杏（PRUNUS ARMENIACA）仁油', 'PRUNUS ARMENIACA (APRICOT) KERNEL OIL', 'Persic Oil，杏仁油，APRICOT KERNEL OIL', 'skin:component:usage:57203688cb465', '高修复性植物油，适用于较为年轻而缺乏油性的干燥肌肤，有引发面疱的可能。含油酸、亚麻仁油酸及维他命A、E，能柔润皮肤并保持弹性。富含不饱和脂肪酸及γ-生育酚，易被皮肤吸收而有良好的滋润效果，可改善干性脱屑等肌肤状态。与空气接触易变质。', '0', '0', '0', '1', '1461740256');
