INSERT INTO `component` VALUES ('1662', '柠檬酸钠', 'SODIUM CITRATE', '枸橼酸钠，Sodium Acid Citrate', 'skin:component:usage:57203683b3e52,skin:component:usage:5720394d9e17a,skin:component:usage:57203a0dd1e09', '酸碱调节剂、抗氧化剂、蛰合剂，在医学上亦作为抗凝血用途。具有良好的pH调节及缓冲性能。柠檬酸钠是一种弱酸强碱盐，与柠檬酸配伍可组成较强的pH缓冲剂，因此在某些不适宜pH大范围变化的场合有其重要用处。', '0', '0', '0', '0', '1461740256');
