INSERT INTO `component` VALUES ('1654', '氧化锌', 'ZINC OXIDE', 'microfine Zinc Oxide, CI 77947', 'skin:component:usage:57203a0b512d4', '紫外线吸收剂，利用反射方式抵抗 UVA、UVB，为物理性粉体防晒成份。白色六角晶体，不溶于水，但可溶于酸性物质[1]。因为它不仅吸附油脂，也会吸附水分，使用后会造成皮肤干燥，具收敛性，针对面疱具有部份制菌、干燥功效，并具有中度遮盖力。几乎可以阻隔掉所有波长的 UVA、UVB，研究显示，粒子越小的氧化锌(例如 microfine zinc oxide 或更小的 nano zinc oxide)，越有', '0', '0', '0', '1', '1461740256');
