INSERT INTO `component` VALUES ('1502', '库拉索芦荟（ALOE BARBADENSIS）提取物', 'ALOE BARBADENSIS EXTRACT', '库拉索芦荟提取物', 'skin:component:usage:5720364128a84,skin:component:usage:57203688cb465,skin:component:usage:572039cbcc577,skin:component:usage:572039eb59c3b', '抗敏成份之一，廣泛被使用於肌膚刀傷、燙傷及多種過敏現象。可有效舒緩肌膚的不適，降低洗臉過程中，因皮膚障壁功能不佳而可能引起的傷害。在臨床實驗上被證實具有許多功效，包括抑制細菌生長、防止發炎、增加傷口癒合速度及修補肌膚組織等，許多功效需待醫學上更進一步發掘與實驗。', '0', '0', '0', '0', '1461740254');
