INSERT INTO `component` VALUES ('1488', '抗坏血酸葡糖苷', 'ASCORBYL GLUCOSIDE', '维他命C配糖体, 维生素C醣甘, 维他命C糖苷, 维生素C糖苷，维他命C葡糖苷，', 'skin:component:usage:57203683b3e52,skin:component:usage:57203a012bbda', '能抑制酪胺酸酵素活性，衛生署規定濃度限量為2％[1]。水性維他命C衍生物，pH呈中性，安定度低~中等，保存上可在產品未拆封前冷藏，以減緩活性降解速度[2]。油水可溶，需用鹼劑調整為中性，pH6~7才能延長活性保存時間。 ', '0', '0', '0', '1', '1461740254');
