INSERT INTO `component` VALUES ('1458', '酵母', 'YEAST', '', 'skin:component:usage:57203688cb465', '含有碳水化合物、乳酸、重要的矿物质及维他命群，可以促进皮肤及黏膜的细胞机能，并能迅速分解污垢、促进肌肤新陈代谢促进细胞再生。', '0', '0', '0', '1', '1461740254');
INSERT INTO `component` VALUES ('1457', '黄原胶', 'XANTHAN GUM', '汉生胶，三仙胶，Xanthum Gum, Xanthen Gum,Zanthan', 'skin:component:usage:5720368644cc0,skin:component:usage:57203688cb465,skin:component:usage:5720395849c7d,skin:component:usage:572039800867a,skin:component:usage:572039bbe5d31,skin:component:usage:57203a12ec9fe', '透明性高，具耐酸、耐盐、耐酵素、耐热性。天然胶体不黏腻，适合于製作不黏产品的增稠剂。', '0', '0', '0', '0', '1461740254');
INSERT INTO `component` VALUES ('1456', '葡萄（VITIS VINIFERA）籽油', 'VITIS VINIFERA (GRAPE) SEED OIL', '', 'skin:component:usage:57203683b3e52,skin:component:usage:57203688cb465,skin:component:usage:5720369d35e0c', '抗自由基、抗氧化及活化細胞，達到長效保濕。清爽好吸收，能促進細胞循環及激勵免疫系統，其所含之原花青素能保持肌膚青春緊緻。', '0', '0', '0', '0', '1461740254');
