INSERT INTO `component` VALUES ('1447', '生育酚（维生素E）', 'TOCOPHEROL', '维他命E，α-生育酚，d-δ-トコフェロール，TOCOPHEROL OIL', 'skin:component:usage:57203683b3e52,skin:component:usage:572039af1147f', '人体之抗氧化剂，脂溶性维他命，存在于多种水果、蔬菜中，维生素E常被用于乳霜和乳液中，因为人们相信其能促进皮肤癒合及减少疤痕形成，具有 8种不同型态，包含 alpha、beta、gamma 及 delta 四种 tocopherols 及四种 tocotrienols，在这其中，一般认为 Alpha-tocopherol 是对人体抗氧化最有效的一种。抗氧化剂，油溶性物质，能避免脂质过氧化，并保护细胞', '0', '0', '0', '0', '1461740254');
