INSERT INTO `component` VALUES ('1435', '生育酚乙酸酯', 'TOCOPHERYL ACETATE', '醋酸盐维他命E, 醋酸生育酚酯, 生育酚醋酸酯，α-Tocopheryl Ace', 'skin:component:usage:57203683b3e52,skin:component:usage:5720369077320', '抗氧化剂，除防止肌肤老化外还可强化美白效果。', '0', '0', '0', '0', '1461740253');
