INSERT INTO `component` VALUES ('1416', '蔗糖硬脂酸酯', 'SUCROSE STEARATE', '硬脂酸蔗糖酯', 'skin:component:usage:5720368644cc0', '天然脂肪酸与蔗糖酯化等到的乳化剂，性质温和，常用于敏感肌肤和婴幼儿护理产品中。', '0', '0', '0', '0', '1461740253');
