INSERT INTO `component` VALUES ('1392', '水杨酸钠', 'SODIUM SALICYLATE', '', 'skin:component:usage:5720396adc2e7', '《化妆品安全技术规范2015版》中作为防腐剂使用，除香波外，不得用于三岁以下儿童使用的产品中，最大允许使用浓度 0.5%(以酸计)，标签上必须标注：含水杨酸，三岁以下儿童勿用。', '0', '0', '0', '0', '1461740253');
