INSERT INTO `component` VALUES ('1376', '氢氧化钠', 'SODIUM HYDROXIDE', '', 'skin:component:usage:57203a0dd1e09', '强碱性物质，用于调节产品 pH 值。可与脂肪酸进行皂化，以制成肥皂，常用于清洁产品中。也常用于烫发和脱毛产品中，含量较高形成碱性环境，使巯基乙酸发挥作用。', '0', '0', '0', '0', '1461740253');
