INSERT INTO `component` VALUES ('1330', '间苯二酚', 'RESORCINOL', '1,3-苯二酚;R-80;间苯二酚-80，1,3-BENZENEDIOL，雷索辛', 'skin:component:usage:572036ace0567,skin:component:usage:5720398795eac,skin:component:usage:572039cbcc577', '具有角质溶解和抗炎作用。能剥离角质、杀菌，常与硫磺剂搭配使用，用于治疗角化型手足癣、头部脂溢性皮炎、痤疮等症，同时对脂溢性脱发和斑秃也有一定疗效，并非预防使用，亦不建议长期使用。限用物质，在化妆品中只能添加于香波、发露、染发膏中使用。', '0', '0', '1', '0', '1461740253');
