INSERT INTO `component` VALUES ('1316', '丙二醇', 'PROPYLENE GLYCOL', '丙二醇, 丙烯乙二醇, 1,2-丙二醇', 'skin:component:usage:572039800867a,skin:component:usage:572039af1147f,skin:component:usage:572039dbb9286', '属于多元醇的一种，小分子保湿成分，但实际上抓水比例并不高。无色无臭液体，能完全溶解于水，不具油腻感，作为溶剂、保湿剂、黏度控制剂，亦有助防腐作用，也能够帮助活性成份渗透。致敏性低，但过量使用可能会对皮肤造成危害。', '0', '0', '0', '0', '1461740253');
