INSERT INTO `component` VALUES ('1299', 'PEG-8 蜂蜡', 'PEG-8 BEESWAX', '', 'skin:component:usage:5720368644cc0', '', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1298', '（动物）胎盘蛋白', 'Placental Protein', '（猪）胎盘蛋白质', 'skin:component:usage:572039af1147f', '动物来源的生物制品，含特硷性磷酸酵素，可帮助细胞的新生功能，使皮肤显得细致、健康。含特殊ALKALINE PHOSPHATASE酵素，可帮助细胞的新生功能，促进毛髮生长。', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1297', '苯氧乙醇', 'PHENOXYETHANOL', 'Phenoxethol, 2-phenoxyethanol，苯氧基乙醇，opti', 'skin:component:usage:572036b48a5b3', '《化妆品卫生规范2007版》《化妆品安全技术规范2015版》作为防腐剂使用，化妆品中最大允许使用浓度1.0%。防腐剂、定香剂，未稀释前对眼睛刺激性很大，稀释至2.2%则不具刺激性。属低度皮肤过敏危险性。', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1296', '矿脂', 'Petrolatum', 'Petroleum Jelly，Petrolatum, Vaseline, So', 'skin:component:usage:5720369d35e0c,skin:component:usage:572039af1147f', '', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1295', '聚乙二醇-8', 'PEG-8', '聚氧乙烯-8，PEG-8', 'skin:component:usage:572039af1147f,skin:component:usage:572039dbb9286', '聚乙二醇类（PEG）主要用作保湿剂，通常以数字作为不同类型区分，如：PEG-4、PEG-100等，数字愈小，代表化学成分愈容易被皮肤吸收。聚乙二醇类具有从大气中吸收并保存水分的能力，可用作湿润剂，能够使皮肤润湿、柔软。PEG结构容易带入致癌物二噁烷，在化妆品中应用时原料纯度十分重要。', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1294', '氢氧化钾', 'POTASSIUM HYDROXIDE', '', 'skin:component:usage:57203a0dd1e09', '强碱性物质，用于调节产品 pH 值。可与脂肪酸进行皂化，以制成肥皂，常用于清洁产品中。也常用于烫发和脱毛产品中，含量较高形成碱性环境，使巯基乙酸发挥作用。', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1293', '紫苏（PERILLA OCYMOIDES）提取物', 'PERILLA OCYMOIDES EXTRACT', '紫苏提取物', 'skin:component:usage:57203688cb465', '', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1292', '珍珠粉', 'PEARL POWDER', '', 'skin:component:usage:57203688cb465', '含有十八种对人体有益的氨基酸，具有美白、润肤、修护的效果。', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1291', '石腊', 'Paraffin', '', 'skin:component:usage:572039800867a', '', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1290', '泛醇', 'Panthenol', '维生素原B5，D-泛醇', 'skin:component:usage:57203676af722,skin:component:usage:572039af1147f', ' 维生素B5的前体，故又称维生素原B5。进入人体内能转化为泛酸，进而合成辅酶A，促进人体蛋白质、脂肪、糖类的代谢，保护皮肤和粘膜，改善毛发光泽，防止疾病的发生。', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1289', '棕榈油酸', 'PALM ACID', '', 'skin:component:usage:5720369d35e0c', '不饱和脂肪酸，防止表皮水份流失，柔润皮肤', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1288', '棕榈酸', 'Palmitic Acid', '十六酸，Hexadecanoic acid，16酸', 'skin:component:usage:5720368644cc0,skin:component:usage:5720369d35e0c', '柔润剂、乳化剂，饱和性脂肪酸，棕榈油中的主要成分，与强碱类皂化后即形成肥皂。与硬脂酸复配使用，可调节膏体或乳液的触变性。也是各种表面活性剂和酯类的重要原料。', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1287', '紫苏（PERILLA FRUTESCEN）提取物', 'PERILLA FRUTESCENS EXTRACT', '白苏提取物', 'skin:component:usage:57203688cb465', '', '0', '0', '0', '0', '1461740253');
INSERT INTO `component` VALUES ('1286', '牡丹（PAEONIA SUFFRUTICOSA）根提取物', 'PAEONIA SUFFRUTICOSA ROOT EXTRACT', '牡丹根提取物，牡丹根提取', 'skin:component:usage:57203688cb465', '活络血液循环，具有抗发炎和抗过敏作用。可预防黑色素生成，且能预防雀斑的产生。并可对抗因过氧化作用而造成的皮肤老化作用。', '0', '0', '0', '1', '1461740253');
INSERT INTO `component` VALUES ('1285', 'PEG-60 氢化蓖麻油', 'PEG-60 HYDROGENATED CASTOR OIL', 'PEG-60 氢化篦麻油', 'skin:component:usage:572036a28c0bb,skin:component:usage:572039dbb9286', '改性的蓖麻油产品，在自然界中易分解，不会有残留。作为常用的高效增溶剂，能将香精,精油等油性物质,均匀地分散到水中，形成稳定、透明的溶液。于化妆水中含量多在 1% 以下，一般健康的皮肤对此浓度通常不会感到刺激，但当皮肤受伤、发炎、起疹、角质偏薄时，部份可溶化剂就可能会造成刺痛、发痒现象。', '0', '0', '0', '0', '1461740253');
