INSERT INTO `component` VALUES ('1161', '白池花（LIMNANTHES ALBA）籽油', 'LIMNANTHES ALBA (MEADOWFOAM) SEED OIL', '白池花籽油', 'skin:component:usage:57203688cb465,skin:component:usage:5720369d35e0c', '长效保湿滋润，改善肌肤干燥，极佳的抗氧化性，创造轻柔、滑爽的肤感。白池花籽油中蜕皮激素具有防止水分流失、再生皮肤作用，是皮肤的新陈代谢活化剂；它通过改变角蛋白组织，调节水合化合物的渗出，重新吸收水分并在表皮形成水合物从而在肌肤表皮锁住水分。', '0', '0', '0', '1', '1461740252');
