INSERT INTO `component` VALUES ('1137', '棕榈酸异丙酯', 'ISOPROPYL PALMITATE', '十六酸异丙酯，IPP', 'skin:component:usage:5720369d35e0c,skin:component:usage:5720395849c7d,skin:component:usage:572039dbb9286,skin:component:usage:57203a03ac818', '属化学合成脂，清爽、易推開，无一般植物油的黏膩感，因分子小，易渗入毛孔中清洁油脂类污垢，作为卸妆用途时效果非常良好，但同时也会渗透角质细胞，过度使用会使皮肤角化、变得粗糙蜡黃。', '0', '1', '0', '0', '1461740251');
