INSERT INTO `component` VALUES ('1076', '甘油聚醚-26', 'GLYCERETH-26', '甘油醇26，Liponic EG-1，PEG-26甘油醚', 'skin:component:usage:5720369591d76,skin:component:usage:572039af1147f,skin:component:usage:572039c3f3184', '作为保湿剂和润滑剂具有光滑，细腻的触感。它可以作为皮肤调理剂使用，与阳离子、非离子以及阴离子表面活性剂有良好的配伍性，并在广泛的PH范围内稳定。除了可作为高效的保湿剂，也是很好的分散媒介，粘度调节剂，可塑剂，泡沫调节剂和出色的滑润剂。', '0', '0', '0', '0', '1461740251');
