INSERT INTO `component` VALUES ('1070', '甘油硬脂酸酯', 'GLYCERYL STEARATE', '甘油硬脂酸，硬脂酸甘油酯，单甘脂，单硬脂酸甘油酯，GLYCERYL MONOST', 'skin:component:usage:5720368644cc0,skin:component:usage:5720369d35e0c', '一般为固体，半固体或稠油状液。在耐热性、粘度等方面较多元醇脂肪酸酯高，耐水解好，具有很强的乳化性能。本身安全、对人体无毒。应用于食品和化妆品。', '0', '0', '0', '0', '1461740251');
