INSERT INTO `component` VALUES ('1060', '甲醛', 'FORMALDEHYDE', 'Merhanal; Oxymerhylene; Methylene oxide', 'skin:component:usage:572036b48a5b3', '化妆品卫生规范中规定：甲醛做为防腐剂最大允许使用浓度为0.2%（口腔产品除外），或0.1%（口腔产品），以上均以游离甲醛计算，喷雾产品禁含甲醛，该限量是指化妆品生产中甲醛做为生产原料带入杂质的限量要求。', '1', '0', '1', '0', '1461740251');
