INSERT INTO `component` VALUES ('1054', '弹性蛋白', 'Elastin', '弹性纤维素，弹性蛋白素，弹性蛋白氨基酸，マリンエラスチン（Marine elas', 'skin:component:usage:57203688cb465,skin:component:usage:572039a3d8aea,skin:component:usage:572039af1147f', '對於肌膚有緊實作用，常被用於營養霜或抗老化霜。動物結締組織中之水解蛋白質，能在人體部份組織拉扯或收縮後使其恢復原型，幫助肌膚於受損或凹陷時恢復彈性，此外亦為身體內部許多器官所不可或缺之物質。', '0', '0', '0', '0', '1461740251');
