INSERT INTO `component` VALUES ('1037', '金钗石斛（DENDROBIUM NOBILE）提取物', 'DENDROBIUM NOBILE EXTRACT', '金钗石斛提取物，新鲜石斛兰萃取', 'skin:component:usage:5720364128a84,skin:component:usage:57203688cb465,skin:component:usage:5720369077320', '抗菌，滋养肌肤，促进肌肤细胞再生。抗衰老，提高免疫力：金钗石斛兰能显著提高超氧化物歧化酶（SOD）水平，降低过氧化脂质（LPO），调节脑单胺类神经介质水平，抑制类似单胺氧化酶（MAO），起到延缓衰老的作用。近些年备受关注的石斛兰多糖也具有显著的免疫增强活性和抗衰老、抗辐射等多种功效。', '0', '0', '0', '0', '1461740250');
