INSERT INTO `component` VALUES ('971', '黄瓜（CUCUMIS SATIVUS）果水', 'CUCUMIS SATIVUS (CUCUMBER) FRUIT WATER', '', 'skin:component:usage:57203688cb465', '富含矿物质、氨基酸、糖蛋白、单宁酸、等人体极易吸收的养分，具有柔软、清新、收敛及保湿作用。有清热解毒，促进血液循环，美白肌肤，减少皱纹，增强皮肤免疫力的功效，对青春痘，粉刺，湿疹，皱纹，烫伤，雀斑，老年斑，皮肤粗糙等问题可以起到修复作用，对发炎的皮肤起到良好的解热作用。', '0', '0', '0', '1', '1461740250');
