INSERT INTO `component` VALUES ('968', '椰油酰胺 DEA', 'COCAMIDE DEA', 'N.N-双羟乙基烷基酰胺、椰油酸二乙醇酰胺、椰子油二乙醇酰胺、烷基醇酰胺,椰油基', 'skin:component:usage:57203968308f1,skin:component:usage:572039800867a,skin:component:usage:572039bbe5d31,skin:component:usage:57203a12ec9fe', '椰子油提取，起泡剂、乳化剂、清洁皮肤及有机酸碱中和剂等。良好的增稠性能和起泡、稳泡作用。', '0', '0', '1', '0', '1461740250');
INSERT INTO `component` VALUES ('967', '椰油酰胺丙基羟基磺基甜菜碱', 'COCAMIDOPROPYL HYDROXYSULTAINE', '', 'skin:component:usage:57203968308f1,skin:component:usage:572039800867a,skin:component:usage:572039bbe5d31,skin:component:usage:57203a03ac818', '温和的两性表面活性剂，刺激性低.改善起泡能力，产生良好又稳定的泡沫.与其它的阴离子表面剂、阳离子表面活性剂和非离子表面活性剂配伍性良好', '0', '0', '0', '0', '1461740250');
