INSERT INTO `component` VALUES ('959', '鲸蜡醇棕榈酸酯', 'CETYL PALMITATE', '棕榈酸十六烷基酯', 'skin:component:usage:5720368644cc0', '', '0', '0', '0', '0', '1461740250');
INSERT INTO `component` VALUES ('958', '鲸蜡基 PEG/PPG-10/1 聚二甲基硅氧烷', 'CETYL PEG/PPG-10/1 DIMETHICONE', 'ABIL EM90，鲸蜡基聚乙醇/聚丙二醇-10/1聚二甲基硅氧烷，鲸蜡基聚乙二', 'skin:component:usage:5720368644cc0', '亮白硅油包水型乳化体用乳化剂，具有极佳的耐热和耐寒稳定性。具有与众不同的宽泛的应用范围：膏霜，软霜，乳液。可以乳化含矿物油脂或不含矿物油脂的配方。能乳化具有高含量的植物油和活性成分的配方。也能乳化具有高含量有机和/或物理防晒剂的配方。', '0', '0', '0', '0', '1461740250');
