INSERT INTO `component` VALUES ('955', '神经酰胺 3', 'CERAMIDE 3', 'Ceramide III，,Ｎ－ステアロイルフィトスフィンゴシン，セラミドＮＰ，', 'skin:component:usage:57203688cb465,skin:component:usage:572039af1147f', '神经酰胺为天然保湿因子，油溶性，为角质层中重要的油性活肤成份，能建立和维持肌肤脂质屏障，重建细胞黏合性，适合作为熟龄肌肤滋润使用。神经酰胺3 为人工合成成份，由 Phytosphingosine(植物鞘氨醇)与硬脂酸酸化后所得，对于肌肤保湿、修护具有良好作用。', '0', '0', '0', '1', '1461740250');
