INSERT INTO `component` VALUES ('942', '金盏花（CALENDULA OFFICINALIS）花提取物', 'CALENDULA OFFICINALIS FLOWER EXTRACT', '天然有机金盏花萃取;金盏花提取液，金盏花萃取物', 'skin:component:usage:5720367c0beb6,skin:component:usage:57203688cb465,skin:component:usage:5720397044c8d', '具舒缓、安抚敏感肌肤等功效。可以增强角质层保水能力。其乙醇提取物有抗菌作用，还有促进真皮和表皮细胞增殖的作用。金盏花提取物会影响月经周期，怀孕和哺乳期内不得使用含有金盏花提取物的护肤品。', '1', '0', '0', '0', '1461740250');
