INSERT INTO `component` VALUES ('816', '牛油果树（BUTYROSPERMUM PARKII）果脂', 'BUTYROSPERMUM PARKII (SHEA BUTTER)', '乳木果油，乳木果脂，乳木果树果脂，乳木果树果酯，牛油果脂，牛油果树，牛油树脂，S', 'skin:component:usage:57203688cb465,skin:component:usage:5720369d35e0c,skin:component:usage:572039af1147f', '厚重的润肤酯，易吸收，富含多种不饱和脂肪酸，能加强皮肤的保湿能力。能很好的滋润轻度干性和重度干性肌肤和角质层受损的肌肤。提取自卡力特树。和其他植物油比较，其含有最高比例的脂肪酸伴随物，特别是生长必要养分的三萜烯醇和维生素E、维生素A和尿囊素。可以防止干燥肌肤水分散失，帮助创伤皮肤愈合，改善皮肤的顺滑度和屏障功能。', '0', '0', '0', '0', '1461740249');
