INSERT INTO `component` VALUES ('811', '丁二醇', 'BUTYLENE GLYCOL', '1,3-丁二醇，1,3 Butylene Glycol, 1.3 Butylen', 'skin:component:usage:57203688cb465,skin:component:usage:5720369d35e0c,skin:component:usage:572039af1147f,skin:component:usage:572039dbb9286', '保湿剂及溶剂，质地温和的多元醇类，十分广泛的被用于化妆用品。小分子保溼成分，可将水分留在角质层，但抓水比例不高。', '0', '0', '0', '0', '1461740249');
