INSERT INTO `component` VALUES ('776', '熊果（ARCTOSTAPHYLOS UVA-URSI）提取物', 'ARCTOSTAPHYLOS UVA-URSI EXTRACT', '熊葡萄提取物，熊莓提取物', 'skin:component:usage:57203a012bbda', '主要成分为熊果苷，熊果苷也可以減少皮膚黑色素的形成，具有美白的功效，而被用於化妝品中。', '0', '0', '1', '0', '1461740249');
INSERT INTO `component` VALUES ('775', '拟南芥（ARABIDOPSIS THALIANA）提取物', 'ARABIDOPSIS THALIANA EXTRACT', '', 'skin:component:usage:57203683b3e52', '', '0', '0', '0', '0', '1461740249');
INSERT INTO `component` VALUES ('774', '白花春黄菊（ANTHEMIS NOBILIS）提取物', 'ANTHEMIS NOBILIS EXTRACT', '洋甘菊提取物, 罗马洋甘菊, 白夏菊，洋甘菊蒸馏液，洋甘菊浸泡液', 'skin:component:usage:5720364128a84,skin:component:usage:5720395d9f1aa,skin:component:usage:572039cbcc577,skin:component:usage:572039eb59c3b,skin:component:usage:57203a012bbda', '含有多种黄酮类化合物、咖啡酸、糖类、多种氨基酸等，具有美白、收缩毛孔、活肤、抗菌、抗炎、抑制皮肤过敏的作用。', '0', '0', '0', '0', '1461740249');
INSERT INTO `component` VALUES ('773', '乙醇（酒精）', 'Alcohol', '酒精，Ethanol，Grain Alcohol，Ethyl Alcohol', 'skin:component:usage:5720363ea46a9,skin:component:usage:572039dbb9286', '易燃无色的化学混合物，在化妆保养品中常用以作为溶剂使用，亦为部份植物萃取方式中不可或缺的成份，使用于肌肤会有部份刺激性。能瞬间收缩毛孔形成清凉感，但效用极短，且利用渗透细胞膜进行去脂方式，长期使用会造成皮肤逐渐黯淡无光泽，由于其刺激性之考量，虽具有良好之辅助卸妆效力，但不宜经常出现在卸妆类产品中。', '0', '0', '1', '0', '1461740249');
