INSERT INTO `component` VALUES ('762', '水', 'AQUA/WATER', 'water ,aqueous,去离子水', 'skin:component:usage:572039dbb9286', '作为其他成份之溶媒、溶剂，一般常见的为纯水(Pure Water)、蒸馏水(Distilled Water)及去离子水(Deionized water)。', '0', '0', '0', '0', '1461740249');
INSERT INTO `component` VALUES ('761', '天门冬（ASPARAGUS COCHINCHINENSIS）根提取物', 'ASPARAGUS COCHINCHINENSIS ROOT EXTRACT', '', 'skin:component:usage:57203683b3e52,skin:component:usage:5720369077320,skin:component:usage:572039af1147f,skin:component:usage:57203a012bbda', '主要成分是皂苷，还含有甾醇类、氨基酸类、天冬多糖、多糖蛋白等。有利于血管的新生和促进、内皮细胞的游走和接着，有利于皮肤创伤的愈合，具有抗氧化、抗衰老、美白、保湿作用。', '0', '0', '0', '1', '1461740249');
INSERT INTO `component` VALUES ('760', '硬脂酸', 'STEARIC ACID', '十八酸', 'skin:component:usage:572039800867a,skin:component:usage:572039d172331,skin:component:usage:57203a12ec9fe', '硬脂酸是化妆品常用原料，从动、植物油脂中得到的固体脂肪酸，有乳化作用，与碱反应可得皂类产品。', '0', '0', '0', '0', '1461740249');
INSERT INTO `component` VALUES ('759', '香精', 'AROMA', '', 'skin:component:usage:572039d93590b', '香精是由两种以上，乃至几十种或近百种香料（天然香料和人造香料），通过一定的调香技术配制而成，对于肌肤有致敏的可能。赋予产品宜人的香气，遮盖原料的气味。化妆品中的香精是日用香精或食用香精。', '0', '0', '1', '0', '1461740249');
