DROP TABLE IF EXISTS `encyclopedia`;
CREATE TABLE `encyclopedia` (
  `unique` varchar(20) NOT NULL,
  `title` varchar(100) DEFAULT NULL,
  `from_nickname` varchar(30) DEFAULT NULL,
  `from_image` varchar(80) DEFAULT NULL,
  `tags` text,
  `image` varchar(80) DEFAULT NULL,
  `thumb` varchar(80) DEFAULT NULL,
  `content` text,
  `pageView` int(255) DEFAULT '0',
  `favnum` int(11) DEFAULT '0',
  `top` tinyint(2) DEFAULT '0',
  `create_at` int(10) DEFAULT NULL,
  `update_at` int(10) DEFAULT NULL,
  `comments` int(255) DEFAULT '0',
  KEY `title` (`title`,`content`(10)) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

